exports.ycLogin = function (req, res, next) {
  if (req.session.userLogin) {
    next();
  } else {
    return res.redirect('/');
  }
};
exports.khongycLogin = function (req, res, next) {
  if (!req.session.userLogin) {
    next();
  } else {
    res.send("<meta><h1>Bạn đã đăng nhập rồi</h1></meta>");
  }
  ;
};
exports.logout = function (req, res, next) {
  if (req.session.userLogin) {
    next();
  } else {
    res.send("<meta><h1>Bạn chưa đăng nhập </h1></meta>");
  }
};
